import sqlite3import loggingfrom typing import List, Dict# Database configurationDB_PATH = "C:/Users/Sorosh/Desktop/DataSet2/squad_contexts.db"logger = logging.getLogger(__name__)def load_contexts_from_db() -> List[Dict]:    """    Fetch all contexts from the SQLite database.    Expects table `contexts` with columns:    - id (INTEGER)    - title (TEXT, nullable)    - text (TEXT)    """    contexts = []    try:        # Open database connection        conn = sqlite3.connect(DB_PATH)        conn.row_factory = sqlite3.Row  # Enable dict-like row access        cursor = conn.cursor()        # Retrieve contexts ordered by insertion id        # noinspection SqlResolve        cursor.execute("""            SELECT id, title, text            FROM contexts            ORDER BY id        """)        rows = cursor.fetchall()        # Normalize rows into application-friendly format        for row in rows:            title = row["title"] or f"Context {row['id']}"            contexts.append({                "id": row["id"],                "title": title,                "text": row["text"].strip()            })        conn.close()        if not contexts:            logger.warning("No contexts found in database")        logger.info("Loaded %d contexts from database", len(contexts))        return contexts    except sqlite3.Error as e:        logger.error("SQLite error while loading contexts: %s", e)        return []    except Exception as e:        logger.error("Unexpected error while loading contexts: %s", e)        return []