import sqlite3from datasets import load_datasetDB_PATH = r"C:\Users\Sorosh\Desktop\DataSet2\squad_contexts.db"# Connect to SQLite database (creates file if it does not exist)conn = sqlite3.connect(DB_PATH)cursor = conn.cursor()# Create contexts table if not already presentcursor.execute("""    CREATE TABLE IF NOT EXISTS contexts (        id INTEGER PRIMARY KEY AUTOINCREMENT,        title TEXT,        text TEXT NOT NULL    )""")# Load SQuAD training splitprint("Loading SQuAD dataset...")dataset = load_dataset("squad", split="train")seen_contexts = set()inserted_count = 0# Insert unique contexts into databaseprint("Inserting contexts...")for example in dataset:    context = example["context"].strip()    title = example["title"]    # Skip duplicate contexts    if context not in seen_contexts:        seen_contexts.add(context)        # noinspection SqlResolve        cursor.execute(            "INSERT INTO contexts (title, text) VALUES (?, ?)",            (title, context)        )        inserted_count += 1        # Log progress every 500 inserts        if inserted_count % 500 == 0:            print(f"Inserted {inserted_count} unique contexts...")# Commit changes and close connectionconn.commit()conn.close()print(f"\nDone! Inserted {inserted_count} unique contexts.")print(f"Database path: {DB_PATH}")